import React from 'react';
import request from 'superagent';
import debounce from 'lodash.debounce';

class InfiniteSpace extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            apods: [],
        };

        window.onscroll = debounce(() => {
            const {
                loadApods
            } = this;

            if (window.innerHeight + document.documentElement.scrollTop === document.documentElement.offsetHeight) {
                loadApods();
            }
        }, 100);

    }
    dayOffset = () => {
        let today = new Date();
        let day = today.setDate(-1 * this.state.apods.length);
        return new Date(day).toISOString().split('T')[0];
    }

    loadApods = () => {
        request
        fetch('api/promotions')
            .then(response => response.json())
            // .then(data => this.setState({ promotions: data, isLoading: false }))
            .then((results) => {
                const nextPromotion = results;

                this.setState({
                    apods: [
                        ...this.state.apods,
                        nextPromotion
                    ]
                });
            });
    }

    render() {
        return (
            <div>
                <h1>Infinite Space!</h1>
                <p>Scroll down to load more!!</p>
                {apods.map(apod => (
                    <React.Fragment key={apod._id}>
                        <hr />
                        <div>
                            {/* <div>{apod.explanation}</div> */}
                            <div>{apod}</div>
                        </div>
                    </React.Fragment>
                ))}

                <hr />

            </div>
        )
    }
}
export default InfiniteSpace;
