import React, { Component } from 'react';
import { Button, ButtonGroup, Container, Table } from 'reactstrap';
import AppNavbar from './AppNavbar';
import { Link } from 'react-router-dom';

class PromotionList extends Component {

  constructor(props) {
    super(props);
    this.state = {promotions: [], isLoading: true};
    this.remove = this.remove.bind(this);
  }

  componentDidMount() {
    this.setState({isLoading: true});

    fetch('api/promotions')
      .then(response => response.json())
      .then(data => this.setState({promotions: data, isLoading: false}));
  }

  async remove(id) {
    await fetch(`/api/promotion/${id}`, {
      method: 'DELETE',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }
    }).then(() => {
      console.log("Remove Done!");
      let updatedPromotions = [...this.state.promotions].filter(i => i._id !== id);
      this.setState({promotions: updatedPromotions});
    });
  }

  render() {
    const {promotions, isLoading} = this.state;

    if (isLoading) {
      return <p>Loading...</p>;
    }

    const promotionList = promotions.map(promotion => {
      return <tr key={promotion._id}>
        <td>{promotion.promotionName}</td>
        <td>{promotion.type}</td>
        <td>{promotion.startDate}</td>
        <td>{promotion.endDate}</td>
        <td>{promotion.userGroupName}</td>
        <td><a href={promotion.copyright}>{promotion.copyright}</a></td>
        <td>
          <ButtonGroup>
            <Button size="sm" color="primary" tag={Link} to={"/promotions/" + promotion._id}>Edit</Button>
            <Button size="sm" color="danger" onClick={() => this.remove(promotion._id)}>Delete</Button>
          </ButtonGroup>
        </td>
      </tr>
    });

    return (
      <div>
        <AppNavbar/>
        <Container fluid>
          <div className="float-right">
            <Button color="success" tag={Link} to="/promotions/new">Add Promotion</Button>
          </div>
          <h3>Promotion List</h3>
          <Table className="mt-4">
            <thead>
              <tr>
                <th width="20%">Promotion Name</th>
                <th width="20%">Type</th>
                <th width="10%">Start Date</th>
                <th>End Date</th>
                <th>userGroupName</th>
                <th width="10%">Actions</th>
              </tr>
            </thead>
            <tbody>
            {promotionList}
            </tbody>
          </Table>
        </Container>
      </div>
    );
  }
}

export default PromotionList;
