import React from 'react';
import request from 'superagent';
import debounce from 'lodash.debounce';

class InfiniteSpace extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            error: false,
            hasMore: true,
            isLoading: false,
            promotions: [],
        };

        window.onscroll = debounce(() => {
            const {
                loadApods,
                state: {
                    error,
                    isLoading,
                    hasMore
                }
            } = this;
            if (error || isLoading || !hasMore) return;

            if (window.innerHeight + document.documentElement.scrollTop === document.documentElement.offsetHeight) {
                loadApods();
            }
        }, 100);

    }

    componentDidMount() {
        this.loadApods();
    }
    loadApods = () => {
        this.setState({ isLoading: true });
        // request
        fetch('api/promotions')
            .then(response => response.json())
            .then(data => this.setState({ promotions: data }))
            // .then((results) => {
            //     const nextPromotion = results;

            //     this.setState({
            //         apods: [
            //             ...this.state.apods,
            //             nextPromotion
            //         ]
            //     });
            // });
            .catch((err) => {
                this.setState({
                    error: err.message,
                    isLoading: false
                });
            });
    }


    render() {
        const { promotions, error, hasMore, isLoading, } = this.state;
        return (
            <div>
                <h1>Infinite Space!</h1>
                <p>Scroll down to load more!!</p>
                {
                    promotions.map(apod => (
                        <React.Fragment key={apod._id}>
                            <hr />
                            <div>
                                {/* <div>{apod.explanation}</div> */}
                                <div>{apod}</div>
                            </div>
                        </React.Fragment>
                    ))}
                < hr />
                {error &&
                    <div style={{ color: '#900' }}>
                        {error}
                    </div>
                }

                {isLoading &&
                    <div>Loading...</div>
                }

                {!hasMore &&
                    <div>Loading Complete</div>
                }
            </div >
        )
    }
}
export default InfiniteSpace;
