import React, { Component } from 'react';
import { Button, ButtonGroup, Container, Table } from 'reactstrap';
import AppNavbar from './AppNavbar';
import { Link } from 'react-router-dom';
import InfiniteScroll from "react-infinite-scroller";

class PromotionList extends Component {

  constructor(props) {
    super(props);
    this.state = { promotions: [], isLoading: true, column: [], items: 5, hasMoreItems: true };
    this.remove = this.remove.bind(this);
  }
  componentDidMount() {
    this.setState({ isLoading: true });
    //////////
    fetch('api/schema')
      .then(response => response.json())
      .then(schema => this.setState({ promotionCoulmn: schema, isLoading: false }));
    fetch('api/promotions')
      .then(response => response.json())
      .then(data => this.setState({ promotions: data, isLoading: false }));
  }
  async duplicate(id) {

  }
  async remove(id) {
    await fetch(`/api/promotion/${id}`, {
      method: 'DELETE',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }
    }).then(() => {
      console.log("Remove Done!");
      let updatedPromotions = [...this.state.promotions].filter(i => i._id !== id);
      this.setState({ promotions: updatedPromotions });
    });
  }
  showItems() {
    fetch('api/promotions/',{  method: 'POST', body:this.state.items})
    .then(response => response.json())
    .then(data => this.setState({ promotions: data, isLoading: false }));
    const { promotions } = this.state;
    console.log("ppppp", promotions);
    var items = [];
    for (var i = 0; i < this.state.items && i < promotions.length; i++) {
      const promotionList = promotions.map(item => (
        items.push(

          <tr key="item._id">{
            Object.values(item).map(value =>
              <td>{item._id != value ? value : ''}</td>
            )}<td>
              <ButtonGroup>
                <Button size="sm" color="primary" tag={Link} to={"/promotions/" + item._id}>Edit</Button>
                {/* <Button size="sm" color="" tag={Link} to={"/promotions/" + item._id}>Duplicate  </Button> */}
                <Button size="sm" color="" onClick={() => this.duplicate(item._id)}>Duplicate</Button>
                <Button size="sm" color="danger" onClick={() => this.remove(item._id)}>Delete</Button>
              </ButtonGroup>
            </td>
          </tr>

        )
      ))
      // items.push(<li key={i}> Item {i} </li>);
      // console.log(i);
    }
    return items;
  }
  loadMore() {
    if (document.documentElement.scrollTop === document.documentElement.offsetHeight) {
      this.setState({ hasMoreItems: false });
    } else {
      setTimeout(() => {
        this.setState({ items: this.state.items + 5 });
      }, 1000);
    }
  }
  render() {
    const { promotionCoulmn, promotions, isLoading } = this.state;
    console.log("promotions", promotions);
    promotions.forEach(item =>
      console.log(Object.keys(item))
    )
    if (isLoading) {
      return <p>Loading...</p>;
    }
    console.log("promotionCoulmn", promotionCoulmn);
    console.log("promotions", promotions);

    const promotionList = promotions.map(item => (
      <tr key="item._id">{
        Object.values(item).map(value =>
          <td>{item._id != value ? value : ''}</td>
        )}<td>
          <ButtonGroup>
            <Button size="sm" color="primary" tag={Link} to={"/promotions/" + item._id}>Edit</Button>
            {/* <Button size="sm" color="" tag={Link} to={"/promotions/" + item._id}>Duplicate  </Button> */}
            <Button size="sm" color="" onClick={() => this.duplicate(item._id)}>Duplicate</Button>
            <Button size="sm" color="danger" onClick={() => this.remove(item._id)}>Delete</Button>
          </ButtonGroup>
        </td></tr>
    ))
    // const promColumn=promotionColumns.map(column=>
    //   // <th>{column.fieldName}</th>
    //  console.log("field",column)

    // )
    // const promColumn = promotionCoulmn.map(c => (
    //   <th>{Object.values(c)[1]}</th>
    //   // Object.values(c).map(cc =>
    //   //   <th>{cc.fieldName}</th>,
    //   //   // console.log("c", cc),
    //   // )
    // ))



    const promotionColumns = [];
    const k = [];
    promotions.map(item => (
      k.push(Object.keys(item)),
      console.log("items", k)
    )
    )
    k.map((item) => (
      console.log(item),
      item.map(a => {
        if (!promotionColumns.some(val => val === a)) {
          promotionColumns.push(a);
          console.log("em", a);
        }
      })
    )
    )
    console.log("promotionC", promotionColumns);
    const p = promotionColumns.map(coulmn =>
      <th>{'_id' != coulmn ? coulmn : ''}</th>
    )
    console.log("p", promotionColumns);
    return (
      <div>
        <AppNavbar />
        <Container fluid>
          <div className="float-right">
            <Button color="success" tag={Link} to="/promotions/new">Add Promotion</Button>
          </div>
          <h3>Promotion List</h3>
          <Table className="mt-4">
            <thead>

              <div style={{ height: '500px', overflow: 'auto' }}>
                <InfiniteScroll
                  loadMore={this.loadMore.bind(this)}
                  hasMore={this.state.hasMoreItems}
                  loader={<div className="loader"> Loading... </div>}
                  useWindow={false}>
                  <tr>{p}</tr>
                  {this.showItems()}{" "}
                </InfiniteScroll>{" "}
              </div>{" "}
            </thead>
            <tbody> </tbody>  </Table>
        </Container>
      </div>
    );
  }
}

export default PromotionList;

