import React, { Component } from "react";
import InfiniteScroll from "react-infinite-scroller";

class Scroll2 extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: 30, promotions: [], hasMoreItems: true, length: 0
        };
    }

    loadMore() {
        if (document.documentElement.scrollTop === document.documentElement.offsetHeight) {
            this.setState({ hasMoreItems: false });
        } else {
            this.setState({ length: this.state.length + 20 });
            // this.getPromotions()
        }
    }
    //    async getPromotions() {
    //         const length = this.state.length;
    //         fetch(`api/promotions/${length}`)
    //             .then(response => { response.json() })
    //             .then(data => this.setState({ promotions: data, isLoading: false }));
    //     }
    showItems() {
        const items = [];
        // const { promotions } = this.state;
        // console.log("promotionsInShowItems", promotions);
        for (var i = 0; i < this.state.items; i++) {
            items.push(<li key={i}> Item {i} </li>);
            console.log(i);
        }
        return items;
    }
    render() {
        const { promotions } = this.state;
        console.log("promotionsScroll", promotions);

        return (
            <div className="App">
                <div style={{ height: '600px', overflow: 'auto' }}>
                    <InfiniteScroll
                        loadPromotions={this.loadMore.bind(this)}
                        hasMore={this.state.hasMoreItems}
                        loader={<div className="loader"> Loading... </div>}
                        useWindow={false}>
                        {this.showItems()}{" "}
                    </InfiniteScroll>{" "}
                </div>{" "}
            </div>
        );
    }
}

export default Scroll2;
