import React, { Component } from "react";
import InfiniteScroll from "react-infinite-scroller";
import { Button, ButtonGroup, Container, Table } from 'reactstrap';
import { Link } from 'react-router-dom';
class Scroll2 extends Component {
    constructor(props) {
        super(props);
        this.state = { promotions: [], items: 20, hasMoreItems: true, length: 40, index: 12 };
        // this.getPromotions = this.getPromotions.bind();
    }
    componentDidMount() {
        const length = this.state.length;
        const index = this.state.index;
        fetch(`api/promotions/${index}`)
            .then(response => {
                // debugger;
                return response.json()
            })
            .then(data => this.setState({ promotions: data, isLoading: false }));
        const { promotions } = this.state;
        console.log("didMount", promotions);
    }
    loadMore() {
        // debugger;
        if (document.documentElement.scrollTop === document.documentElement.offsetHeight) {
            this.setState({ hasMoreItems: false });
        } else {
            if (this.state.index < 60) {
                setTimeout(() => {
                    this.setState({ length: this.state.length + 5 });
                    this.setState({ index: this.state.index + 5 });
                    // this.setState({ items: this.state.items + 10 });
                    // console.log("items length", this.state.items, this.state.length);
                    // debugger;
                    // this.getPromotions();
                    this.componentDidMount()
                    const { promotions } = this.state;
                    console.log("loadMore", promotions);
                }, 2000);
            }
        }
    }
    async getPromotions() {
        // debugger;
        const length = this.state.length;
        const index = this.state.index;
        fetch(`api/promotions/${22}`)
            .then(response => { response.json() })
            .then(data => this.setState({ promotions: data }));
        const { promotions } = this.state;
        console.log("getPromotions", index, promotions);
    }
    showItems() {
        const items = [];
        // this.getPromotions();
        const { promotions } = this.state;
        console.log("promotionsInShowItems", promotions);
        // for (var i = this.state.length - 40; i < this.state.length; i++) {
        //     items.push(<li key={i}> Item {i} </li>);
        //     console.log(i);
        // }
        // return items;
        promotions.map(item => (
            items.push(
                <tr key={item._id}>{
                    Object.values(item).map(value =>
                        <td>{item._id != value ? value : ''}</td>
                    )}<td>
                        <ButtonGroup>
                            <Button size="sm" color="primary" tag={Link} to={"/promotions/" + item._id}>Edit</Button>
                            {/* <Button size="sm" color="" tag={Link} to={"/promotions/" + item._id}>Duplicate  </Button> */}
                            <Button size="sm" color="" onClick={() => this.duplicate(item._id)}>Duplicate</Button>
                            <Button size="sm" color="danger" onClick={() => this.remove(item._id)}>Delete</Button>
                        </ButtonGroup>
                        <br></br>
                        <br></br>
                        <br></br>
                        <br></br>
                        <br></br>

                    </td>
                </tr>

            )
        ))
        return items;
    }
    render() {
        const { promotions } = this.state;
        console.log("promotionsScroll", promotions);

        return (
            <div className="App">
                <div style={{ height: '600px', overflow: 'auto' }}>
                    <InfiniteScroll
                        loadMore={this.loadMore.bind(this)}
                        hasMore={this.state.hasMoreItems}
                        loader={<div className="loader"> Loading... </div>}
                        useWindow={false}>
                        {this.showItems()}{" "}
                    </InfiniteScroll>{" "}
                </div>{" "}
            </div>
        );
    }
}

export default Scroll2;
